// in decorate

//===========================================================================
//
// Orange Card
//
//===========================================================================
ACTOR OrangeCard : Key 100
{
   Inventory.PickupMessage "Picked up an orange keycard."
   Inventory.Icon "OKEYA0"
   States
   {
   Spawn:
      OKEY A 10
      OKEY B 10 BRIGHT
   }
}

// in lockdefs lump
// key number는 1과 255 사이, 단 기존 1,2,3,4,5,6,100,101129,130,131,132,133,134,229 제외
Lock 7 
{
   OrangeCard
   Message "You need an orange card to open this door"
   Mapcolor 255 128 0
}
